# Candidate solver and test harness for validation
from collections import deque

# Candidate solve function from the provided code
def candidate_solve(n, k, s):
    cells = list(s)
    operations = 0

    i = 0
    while i < n:
        if cells[i] == 'B':
            for j in range(i, min(i + k, n)):
                cells[j] = 'W'
            operations += 1
            i += 1
        else:
            i += 1

    return operations

# Brute-force correct solver using BFS on bitmasks
# Return minimal number of operations to make all cells white
# States: bitmask where 1 is black, 0 is white

def brute_solve(n, k, s):
    start = 0
    for i, ch in enumerate(s):
        if ch == 'B':
            start |= (1 << i)
    if start == 0:
        return 0
    max_start = n - k
    # Precompute operation masks for valid starts
    op_masks = []
    for st in range(0, max_start + 1):
        mask = 0
        for j in range(st, st + k):
            mask |= (1 << j)
        op_masks.append(mask)

    # BFS
    q = deque()
    q.append((start, 0))
    seen = {start}
    while q:
        mask, d = q.popleft()
        if mask == 0:
            return d
        for om in op_masks:
            new_mask = mask & (~om)
            if new_mask not in seen:
                seen.add(new_mask)
                q.append((new_mask, d + 1))
    return float('inf')

# Run exhaustive tests for n from 1..8
mismatches = []
for n in range(1, 9):
    for k in range(1, n + 1):
        total = 1 << n
        for mask in range(total):
            s = ''.join('B' if ((mask >> i) & 1) else 'W' for i in range(n))
            c = candidate_solve(n, k, s)
            b = brute_solve(n, k, s)
            if c != b:
                mismatches.append((n, k, s, c, b))

# Output results
if not mismatches:
    print("All tests passed for n up to 8")
else:
    print(f"Found {len(mismatches)} mismatches (showing up to 50):")
    for item in mismatches[:50]:
        n, k, s, c, b = item
        print(f"n={n}, k={k}, s={s}, candidate={c}, brute={b}")
    # Save mismatches to a file for inspection
    with open('/workspace/mismatches.txt', 'w') as f:
        for item in mismatches:
            n, k, s, c, b = item
            f.write(f"n={n}, k={k}, s={s}, candidate={c}, brute={b}\n")

