from typing import List

class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        memo = {}

        def dp(mask: int, last_idx: int) -> int:
            if mask == (1 << n) - 1:
                return 1
            key = (mask, last_idx)
            if key in memo:
                return memo[key]
            res = 0
            last_val = nums[last_idx]
            for i in range(n):
                if mask & (1 << i):
                    continue
                if last_val % nums[i] == 0 or nums[i] % last_val == 0:
                    res = (res + dp(mask | (1 << i), i)) % MOD
            memo[key] = res
            return res

        total = 0
        for i in range(n):
            total = (total + dp(1 << i, i)) % MOD
        return total

