from pathlib import Path
code = Path('/workspace/solution.py').read_text()
globals_dict = {}
exec(code, globals_dict)
Solution = globals_dict['Solution']
from collections import Counter

s = Solution()

# brute function

def brute_min_deletions(word, k):
    freq = Counter(word)
    letters = list(freq.keys())
    n = len(word)
    m = len(letters)
    best = n
    for mask in range(1 << m):
        deletions_removed = 0
        kept_freqs = []
        for i in range(m):
            if (mask >> i) & 1:
                kept_freqs.append(freq[letters[i]])
            else:
                deletions_removed += freq[letters[i]]
        if not kept_freqs:
            best = min(best, n)
            continue
        maxf = max(kept_freqs)
        for L in range(maxf + 1):
            valid = True
            deletions = deletions_removed
            for f in kept_freqs:
                if f < L:
                    valid = False
                    break
                if f > L + k:
                    deletions += f - (L + k)
            if valid:
                best = min(best, deletions)
    return best

letters = ['a','b','c','d']
from itertools import product

count=0
for n in range(0,8):
    for tup in product(letters, repeat=n):
        word = ''.join(tup)
        for k in range(0,5):
            out = s.minimumDeletions(word, k)
            brute = brute_min_deletions(word, k)
            if out != brute:
                print('Mismatch!', repr(word), k, out, brute)
                raise SystemExit('Failed')
        count += 1
print('All exhaustive tests passed for {} strings'.format(count))

