import unittest
from solution import Solution

class TestEncryption(unittest.TestCase):
    def setUp(self):
        self.sol = Solution()

    def test_examples(self):
        self.assertEqual(self.sol.getEncryptedString("dart", 3), "tdar")
        self.assertEqual(self.sol.getEncryptedString("aaa", 1), "aaa")

    def test_single_char(self):
        self.assertEqual(self.sol.getEncryptedString("a", 5), "a")

    def test_full_rotation(self):
        self.assertEqual(self.sol.getEncryptedString("abcd", 4), "abcd")

    def test_large_k(self):
        self.assertEqual(self.sol.getEncryptedString("abcdef", 10), "efabcd")

    def test_random(self):
        s = "abcdefghijklmnopqrstuvwxyz"
        self.assertEqual(self.sol.getEncryptedString(s, 1), s[1:]+s[:1])
        self.assertEqual(self.sol.getEncryptedString(s, 25), s[-25:]+s[:-25])

    def test_not_letter(self):
        # Problem constraints say only lowercase, but test behavior
        self.assertEqual(self.sol.getEncryptedString("ab-cd", 2), "-cdab")

if __name__ == '__main__':
    unittest.main()
PY

