import heapq
from math import isqrt

def is_prime(num):
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False
    r = int(num**0.5)
    for i in range(3, r+1, 2):
        if num % i == 0:
            return False
    return True


def correct_min_ops(n,m):
    # if n and m have different digit lengths, it's invalid per problem constraints
    sN=str(n); sM=str(m)
    if len(sN)!=len(sM):
        return -1
    if is_prime(n) or is_prime(m):
        return -1
    if n==m:
        return n
    num_digits=len(sN)
    # Dijkstra where cost to move u->v is v
    dist = {n: n}
    pq=[(n,n)] # (cost,node)
    while pq:
        cost,u = heapq.heappop(pq)
        if cost!=dist.get(u, None):
            continue
        if u==m:
            return cost
        s = str(u).zfill(num_digits)
        for i in range(num_digits):
            d = int(s[i])
            for nd in (d+1, d-1):
                if 0<=nd<=9:
                    ns = s[:i]+str(nd)+s[i+1:]
                    # leading zeros not allowed
                    if len(ns.lstrip('0'))!=num_digits:
                        # if ns all zeros then length of int(ns) will be 1 not equal to num_digits
                        # easier: check if ns[0]=='0': skip
                        if ns[0]=='0':
                            continue
                    v = int(ns)
                    if is_prime(v):
                        continue
                    new_cost = cost + v
                    if v not in dist or new_cost < dist[v]:
                        dist[v] = new_cost
                        heapq.heappush(pq, (new_cost, v))
    return -1

