import subprocess
import itertools
import random
import sys

MOD = 998244353

# Helper functions

def is_polish(seq):
    need = 1
    for x in seq:
        if need == 0:
            return False
        need = need - 1 + x
        if need < 0:
            return False
    return need == 0


def leq_lex(s, a):
    # both lists of same length
    for x, y in zip(s, a):
        if x < y:
            return True
        if x > y:
            return False
    return True  # equal


def brute_count(N, A):
    count = 0
    for s in itertools.product(range(N), repeat=N):
        if is_polish(s) and leq_lex(s, A):
            count += 1
    return count % MOD


def run_solution(N, A):
    inp = str(N) + "\n" + " ".join(map(str, A)) + "\n"
    try:
        p = subprocess.run([sys.executable, '/workspace/solution.py'], input=inp, text=True, capture_output=True, timeout=10)
        if p.returncode != 0:
            return None, p.stderr
        out = p.stdout.strip()
        return int(out), None
    except Exception as e:
        return None, str(e)


def test_case(N, A):
    expected = brute_count(N, A)
    actual, err = run_solution(N, A)
    return expected, actual, err


random.seed(0)

failed = []

# Test sample from prompt
N = 6
A = [1,1,1,2,0,0]
exp, act, err = test_case(N, A)
print('Sample:', exp, act, err)
if exp != act:
    failed.append(('sample', N, A, exp, act, err))

# Test small N
for N in range(1,7):
    tests = []
    # all zeros
    tests.append([0]*N)
    # all max
    tests.append([N-1]*N)
    # alternating
    tests.append([i%N for i in range(N)])
    # randoms
    for _ in range(5):
        tests.append([random.randrange(N) for _ in range(N)])

    for A in tests:
        exp, act, err = test_case(N, A)
        if err is not None:
            print('Error running solution', N, A, err)
            failed.append(('error', N, A, err))
            continue
        if exp != act:
            failed.append(('mismatch', N, A, exp, act))
            print('Mismatch for N=', N, 'A=', A, 'expected=', exp, 'actual=', act)

print('Total tests:', sum(1 for _ in range(0)))
print('Failed count:', len(failed))
if failed:
    for f in failed[:10]:
        print(f)
else:
    print('All tests passed')

