# brute force solver: returns True if Fennec (first player) wins, False if Snuke wins

def winner(a):
    n = len(a)
    full_mask = (1<<n)-1
    from functools import lru_cache
    @lru_cache(None)
    def dfs(a_tuple, visited_mask, turn):
        # If visited_mask already full => previous player won, so current loses
        if visited_mask == full_mask:
            return False
        # Try all possible moves
        for i in range(n):
            if a_tuple[i] >= 1:
                # perform move
                new_a = list(a_tuple)
                new_a[i] -= 1
                new_mask = visited_mask
                if not (visited_mask & (1<<i)):
                    new_mask |= (1<<i)
                # if new_mask full => current player wins
                if new_mask == full_mask:
                    return True
                # else check opponent
                opp_wins = dfs(tuple(new_a), new_mask, 1-turn)
                if not opp_wins:
                    return True
        return False
    return dfs(tuple(a), 0, 0)

