mismatch_cases = []
checked = 0
import itertools
for n in range(1,7):
    for a in itertools.product(range(1,4), repeat=n):
        checked += 1
        actual = winner(list(a))
        guess = (n % 2 == 1)
        if actual != guess:
            mismatch_cases.append((a, actual))

print('Checked cases:', checked)
print('Mismatches count:', len(mismatch_cases))
print('Some mismatches (up to 20):')
for idx,(a,actual) in enumerate(mismatch_cases[:20]):
    print(idx+1, 'n=', len(a), 'a=', a, 'actual=', 'Fennec' if actual else 'Snuke')

