# brute force optimum for small arrays

def opt(arr):
    n = len(arr)
    @lru_cache(None)
    def dp(state):
        if len(state) <= 1:
            return 0
        best = 0
        for i in range(len(state)-1):
            score = abs(state[i] - state[i+1]) + dp(state[:i] + state[i+2:])
            if score > best:
                best = score
        return best
    return dp(tuple(arr))

# try to observe patterns for small N

def compute_B(arr):
    return [arr[i] * (1 if (i%2==0) else -1) for i in range(len(arr))]

# Attempt greedy: stack-based pairing on B

def greedy_stack(arr):
    B = compute_B(arr)
    stack = [] # store indices
    score = 0
    for i in range(len(B)):
        stack.append(i)
        # try to reduce while top two stack elements form a pair beneficial?
        # we will attempt pairing top two whenever possible (i.e., they are adjacent in compressed sequence)
        # but that just pairs neighbors in original sequence, trivial
        while len(stack) >= 2:
            j = stack[-2]
            k = stack[-1]
            # if we pair j and k now, we would remove them - but pairing j and k is only allowed if they are adjacent in current sequence -> they are adjacent in compressed sequence
            # greedy rule: pair (j,k) if |B[j]+B[k]| >= ???
            # Let's use simple rule: pair if B[j] * B[k] >= 0 (same sign)
            if B[j] * B[k] >= 0:
                score += abs(B[j] + B[k])
                stack.pop(); stack.pop()
                # after pairing, continuation
            else:
                break
    return score

# test this greedy on random arrays small

def test_random(num_tests=1000, n=8):
    for _ in range(num_tests):
        arr = [random.randint(0,9) for _ in range(n)]
        g = greedy_stack(arr)
        o = opt(arr)
        if g != o:
            return arr, g, o
    return None

# run tests
for n in range(2,8):
    res = test_random(2000, n)
    print('n=',n,'res=',res)

# try different greedy rules

