import itertools

# compute exact optimal by brute force (dp)
def opt(arr):
    @lru_cache(None)
    def dp(state):
        if len(state) <= 1:
            return 0
        best = 0
        for i in range(len(state)-1):
            score = abs(state[i]-state[i+1]) + dp(state[:i] + state[i+2:])
            if score > best: best = score
        return best
    return dp(tuple(arr))

# compute best over balanced parentheses sequences
# Balanced sequences of length n even: sequences of n/2 '(' and n/2 ')'

def best_balanced(arr):
    n = len(arr)
    best = -10**18
    # generate all sequences of +1/-1 of length n with prefix sum >=0 and final sum 0
    def gen(n, open_count, close_count, seq, pref):
        nonlocal best
        if open_count + close_count == n:
            if pref == 0:
                ssum = sum(x*y for x,y in zip(seq, arr))
                if ssum > best: best = ssum
            return
        # try '(' => +1
        if open_count < n//2:
            gen(n, open_count+1, close_count, seq+[1], pref+1)
        # try ')' => -1 only if pref>0
        if close_count < n//2 and pref>0:
            gen(n, open_count, close_count+1, seq+[-1], pref-1)

    if n%2==1:
        # for odd n, balanced sequences don't exist; could have final pref=1 maybe? We'll skip.
        return None
    gen(n,0,0,[],0)
    return best

# test small all arrays up to n=6 values in small domain
for n in range(2,8,2):
    for arr in itertools.product(range(3), repeat=n):
        arr = list(arr)
        o = opt(arr)
        b = best_balanced(arr)
        if b is None:
            continue
        if o != b:
            print('n=',n,'arr=',arr,'opt=',o,'balanced=',b)
            raise SystemExit
print('Equal for small cases (even n up to 6)')

